    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title 'Accueil' %}

{% block body %}

    <div class=\"jumbotron jumbotron-home text-center\">
        <div class=\"container mt-5\">

            {% for message in app.flashes ('success') %}
                <div class=\"alert alert-success\">
                    {{ message }}
                </div>           
            {% endfor %}

            <h1>L'Agence Immo</h1>
            <p style=\"font-family: Montserrat, Bold;\">L'AgenceImmo vous propose des annonces immobilières de ventes pour votre projet immobilier d'achat, d'investissement ou de location. Si vous souhaitez mettre en vente votre logement, contactez nous pour une estimation de votre bien immobilier.</div>
    </div>

    <div class=\"home\">
        <div class=\"container mt-4\">
            <h2>Nos derniers biens</h2>
            <hr>
            <div class=\"row flex\">
            {% for property in properties %}
                <div class=\"col\">
                    {% include \"property/_property.html.twig\" %}
                </div>
            {% endfor %}
        </div>
    </div>

{% endblock %}

", "pages/home.html.twig", "/Applications/MAMP/htdocs/AgenceImmo/templates/pages/home.html.twig");
